TCName.Artdet:
select a.matnr, a.MEINS,a.VRKME,sum(a.lfimg), sum(a.ZZDESPATCH_WEIGHT), max(a.OM_SIZE),b.DISPLAY_ITEM_IND, c.IS_CPBD,c.RANDOM_WEIGHT_FLG from ZWOWO_ORDER_NOTIF_ITM a 
JOIN zwowc_article_master b on a.matnr = b.matnr JOIN ZWOWC_ARTICLE_LISTING c on b.matnr = c.matnr
 group by a.matnr, a.meins, a.vbeln, a.VRKME,b.DISPLAY_ITEM_IND, c.IS_CPBD,c.RANDOM_WEIGHT_FLG having a.vbeln = 'ord_no' ORDER BY a.matnr ASC;

TCName.Artdet_1:
select a.matnr, a.MEINS,a.VRKME,sum(a.lfimg), sum(a.ZZDESPATCH_WEIGHT), max(a.OM_SIZE),b.DISPLAY_ITEM_IND, c.IS_CPBD,b.RANDOM_WEIGHT_FLG from ZWOWO_ORDER_NOTIF_ITM a 
JOIN zwowc_article_master b on a.matnr = b.matnr JOIN ZWOWC_ARTICLE_LISTING c on b.matnr = c.matnr
 group by a.matnr, a.meins, a.vbeln, a.VRKME,b.DISPLAY_ITEM_IND, c.IS_CPBD,b.RANDOM_WEIGHT_FLG having a.vbeln = 'ord_no' ORDER BY a.matnr ASC;

TCName.Artdet:
select a.matnr, a.MEINS,a.VRKME,sum(a.lfimg), sum(a.ZZDESPATCH_WEIGHT), max(a.OM_SIZE),b.DISPLAY_ITEM_IND, c.IS_CPBD,c.RANDOM_WEIGHT_FLG, b.temperature_ind,c.pi_om from ZWOWO_ORDER_NOTIF_ITM a 
JOIN zwowc_article_master b on a.matnr = b.matnr JOIN ZWOWC_ARTICLE_LISTING c on b.matnr = c.matnr
 group by a.matnr, a.meins, a.vbeln, a.VRKME,b.DISPLAY_ITEM_IND, c.IS_CPBD,c.RANDOM_WEIGHT_FLG, b.temperature_ind, c.pi_om having a.vbeln = 'ord_no' ORDER BY a.matnr ASC;
