TCName.RecvDispArtDet:
select LRI.matnr, convert(DOUBLE,LRI.MENGE) || ' ' || LRI.MEINH as recvd_qty, 
convert(DOUBLE, LRI.MENGE) * convert(DOUBLE, LRI.RECV_OM) || ' ' || art_uom.meinh Tot_rec_units,
convert(DOUBLE, LRI.RECV_OM) || ' ' || art_uom.meinh as RECV_OM, LRI.display_article, LRI.RECV_METHOD, convert(DOUBLE, LRI.MENGE) * convert(DOUBLE, LRI.RECV_OM), LRI.MEINH, convert(DOUBLE, LRI.RECV_OM) 
 from LOCAL_RECEIVING_INFO LRI  JOIN ZWOWC_ARTICLE_LISTING ZAL ON (LRI.MATNR = ZAL.MATNR) 
JOIN ZWOWC_ARTICLE_MASTER ZAM ON (LRI.MATNR = ZAM.MATNR) join ZWOWC_ARTICLE_UOM art_uom on (ZAL.MATNR=art_uom.matnr) 
WHERE LRI.EBELN = 'ord_no' and ART_UOM.base_uom_ind = 'Y' and display_article ='art_no';

select LRI.matnr, convert(DOUBLE,LRI.MENGE) || ' ' || LRI.MEINH as recvd_qty, 
convert(DOUBLE, LRI.MENGE) * convert(DOUBLE, LRI.RECV_OM) || ' ' || art_uom.meinh Tot_rec_units,
convert(DOUBLE, LRI.RECV_OM) || ' ' || art_uom.meinh as RECV_OM, LRI.display_article, LRI.RECV_METHOD, convert(DOUBLE, LRI.MENGE) * convert(DOUBLE, LRI.RECV_OM), LRI.MEINH, convert(DOUBLE, LRI.RECV_OM) 
 from LOCAL_RECEIVING_INFO LRI  JOIN ZWOWC_ARTICLE_LISTING ZAL ON (LRI.MATNR = ZAL.MATNR) 
JOIN ZWOWC_ARTICLE_MASTER ZAM ON (LRI.MATNR = ZAM.MATNR) join ZWOWC_ARTICLE_UOM art_uom on (ZAL.MATNR=art_uom.matnr) 
WHERE LRI.EBELN = 'ord_no' and ART_UOM.base_uom_ind = 'Y' and display_article ='art_no';

TCName.OrdDet:
select PITM.EBELN,PITM.MATNR,PITM.MENGE,PITM.MEINS,ZAL.RANDOM_WEIGHT_FLG, ZAL.PI_OM,PITM.pack_size, ZAL.IS_CPBD,
ZAL.COMPLEX_PACK_BRK_ARTICLE, ZAM.DISPLAY_ITEM_IND, ZAM.TEMPERATURE_IND, case when PITM.PI_QTY > 0 THEN CONVERT(double, PITM.PI_QTY) ELSE 0 END as PI_QTY FROM ZWOWO_PO_ITM PITM  
JOIN ZWOWC_ARTICLE_LISTING ZAL ON (PITM.MATNR = ZAL.MATNR) JOIN ZWOWC_ARTICLE_MASTER ZAM ON (PITM.MATNR = ZAM.MATNR) WHERE PITM.EBELN = 'ord_no';

select PITM.EBELN,PITM.MATNR,PITM.MENGE,PITM.MEINS,ZAL.RANDOM_WEIGHT_FLG, ZAL.PI_OM,PITM.pack_size, ZAL.IS_CPBD,
ZAL.COMPLEX_PACK_BRK_ARTICLE, ZAM.DISPLAY_ITEM_IND, ZAM.TEMPERATURE_IND,case when PITM.PI_QTY > 0 THEN CONVERT(double, PITM.PI_QTY) ELSE 0 END as PI_QTY FROM ZWOWO_PO_ITM PITM 
JOIN ZWOWC_ARTICLE_LISTING ZAL ON (PITM.MATNR = ZAL.MATNR) JOIN ZWOWC_ARTICLE_MASTER ZAM ON (PITM.MATNR = ZAM.MATNR) WHERE PITM.EBELN = 'ord_no';

select PITM.EBELN,PITM.MATNR,PITM.MENGE,PITM.MEINS,ZAL.RANDOM_WEIGHT_FLG, ZAL.PI_OM,PITM.pack_size, ZAL.IS_CPBD,
ZAL.COMPLEX_PACK_BRK_ARTICLE, ZAM.DISPLAY_ITEM_IND, ZAM.TEMPERATURE_IND, case when PITM.PI_QTY > 0 THEN CONVERT(double, PITM.PI_QTY) ELSE 0 END as PI_QTY FROM ZWOWO_PO_ITM PITM 
JOIN ZWOWC_ARTICLE_LISTING ZAL ON (PITM.MATNR = ZAL.MATNR) JOIN ZWOWC_ARTICLE_MASTER ZAM ON (PITM.MATNR = ZAM.MATNR) WHERE PITM.EBELN = 'ord_no';

select PITM.EBELN,PITM.MATNR,PITM.MENGE,PITM.MEINS,ZAL.RANDOM_WEIGHT_FLG, ZAL.PI_OM,PITM.pack_size, ZAL.IS_CPBD,
ZAL.COMPLEX_PACK_BRK_ARTICLE, ZAM.DISPLAY_ITEM_IND,ZAL.ALC_STATUS, case when PITM.PI_QTY > 0 THEN CONVERT(double, PITM.PI_QTY) ELSE 0 END as PI_QTY FROM ZWOWO_PO_ITM PITM 
JOIN ZWOWC_ARTICLE_LISTING ZAL ON (PITM.MATNR = ZAL.MATNR) JOIN ZWOWC_ARTICLE_MASTER ZAM ON (PITM.MATNR = ZAM.MATNR) WHERE PITM.EBELN = 'ord_no';

TCName.DispArtOrd:
