SUM(ZOC.ARTICLE_QTY) OVER (PARTITION BY ZOC.DISPLAY_ARTICLE,ZOC.MATNR,ZOC.MEINS) AS QTY
FROM ZWOWO_ORDER_NOTIF_HDR ZONH JOIN ZWOWO_ORDER_COMPONENT ZOC ON (ZONH.VBELN = ZOC.EBELN) WHERE ZONH.VBELN = 'ord_no' and ZOC.DISPLAY_ARTICLE = 'art_no'  and ZOC.order_type = 'DLY';

